import React, { Component } from 'react';

export class Carousel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            currentItemIndex: 0,
            pokemonList : [],
            fetched : false,
            loading : false
        }
    }

    prevSlide = () => {
        // find the index of the last image in the array
        const lastIndex = this.state.items.length - 1;
        // check if we need to start over from the last index again
        const resetIndex = this.state.currentItemIndex === 0;
        const index = resetIndex ? lastIndex : this.state.currentItemIndex - 3;
        // assign the logical index to currentItemIndex that will use in render method
        this.setState({
            currentItemIndex: index
        })
    }

    nextSlide = () => {
        // find the index of the last image in the array
        const lastIndex = this.state.pokemonList.length - 1;
        // check if we need to start over from the first index
        const resetIndex = this.state.currentItemIndex === lastIndex;
        const index = resetIndex ? 0 : this.state.currentItemIndex + 3;
        // assign the logical index to currentItemIndex that will use in render method
        this.setState({
            currentItemIndex: index
        });
    }

    componentDidMount() {

        fetch('http://pokeapi.co/api/v2/pokemon?limit=51').then(res=>res.json())
            .then(response=>{
                this.setState({
                    pokemonList : response.results,
                    loading : true,
                    fetched : true
                });
            });
    }

    render() {
        const {fetched, loading, pokemonList} = this.state;

        console.log(pokemonList);

        // get current image index
        const index = this.state.currentItemIndex;
        let firstThreeItems = pokemonList.slice(index, index + 3);

        if (firstThreeItems.length < 3) {
            firstThreeItems = firstThreeItems.concat(this.state.pokemonList.slice(0, 3 - firstThreeItems.length))
        }

        if(!fetched) {
            return (
                <div>...Loading</div>
            )
        } else {
            return (
                <div className='container'>
                    <div className="card-columns">
                        {firstThreeItems.map((item, index) => <Pokemon key={pokemon.name} id={index+1} pokemon={pokemon}/>(
                            <div className="card" key={index}>
                                <div className="card-header bg-success">
                                    <span className="float-sm-left"> {item.name}  </span> <span className="text-right">ID : {index + 1}</span>
                                </div>
                                <div className="card-body">
                                    <div className="card-img">
                                        <img src={item.image} alt=""/>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="row d-flex">
                        <div className="mr-auto">
                            <button onClick={this.prevSlide} className="btn btn-success">Previous</button>
                        </div>
                        <div className="p-2">
                            <button onClick={this.nextSlide} className="btn btn-success">Next</button>
                        </div>
                    </div>
                </div>
            )
        }
    }
}
