import React, { Component } from 'react';

export class Carousel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            currentImageIndex: 0,
            images: [
                'https://via.placeholder.com/200x150?text=first',
                'https://via.placeholder.com/200x150?text=second',
                'https://via.placeholder.com/200x150?text=third',
                'https://via.placeholder.com/200x150?text=fourth',
                'https://via.placeholder.com/200x150?text=fifth',
                'https://via.placeholder.com/200x150?text=sixth',
                'https://via.placeholder.com/200x150?text=seventh',
                'https://via.placeholder.com/200x150?text=eighth',
                'https://via.placeholder.com/200x150?text=ninth',
                'https://via.placeholder.com/200x150?text=tenth'
            ],
            items: [
                {
                    id:1,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=first'
                },
                {
                    id:2,
                    name:'IVYSAUR',
                    image: 'https://via.placeholder.com/200x150?text=second'
                },
                {
                    id:3,
                    name:'VENUSAUR',
                    image: 'https://via.placeholder.com/200x150?text=third'
                },
                {
                    id:4,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=forth'
                },
                {
                    id:5,
                    name:'IVYSAUR',
                    image: 'https://via.placeholder.com/200x150?text=fifth'
                },
                {
                    id:6,
                    name:'VENUSAUR',
                    image: 'https://via.placeholder.com/200x150?text=sixth'
                },
                {
                    id:7,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=seventh'
                },
                {
                    id:8,
                    name:'IVYSAUR',
                    image: 'https://via.placeholder.com/200x150?text=eighth'
                },
                {
                    id:9,
                    name:'VENUSAUR',
                    image: 'https://via.placeholder.com/200x150?text=ninth'
                },
                {
                    id:10,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=tenth'
                }
            ]
        }
    }

    prevSlide = () => {
        // find the index of the last image in the array
        const lastIndex = this.state.images.length - 1;
        // check if we need to start over from the last index again
        const resetIndex = this.state.currentImageIndex === 0;
        const index = resetIndex ? lastIndex : this.state.currentImageIndex - 3;
        // assign the logical index to currentImageIndex that will use in render method
        this.setState({
            currentImageIndex: index
        })
    }

    nextSlide = () => {
        // find the index of the last image in the array
        const lastIndex = this.state.images.length - 1;
        // check if we need to start over from the first index
        const resetIndex = this.state.currentImageIndex === lastIndex;
        const index = resetIndex ? 0 : this.state.currentImageIndex + 3;
        // assign the logical index to currentImageIndex that will use in render method
        this.setState({
            currentImageIndex: index
        });
    }

    render() {
        // get current image index
        const index = this.state.currentImageIndex;
        let firstThreeVideo = this.state.images.slice(index, index + 3);

        if (firstThreeVideo.length < 3) {
            firstThreeVideo = firstThreeVideo.concat(this.state.images.slice(0, 3 - firstThreeVideo.length))
        }
        return (
            <div className='container'>
                <button onClick = {this.prevSlide}>Previous</button>

                {firstThreeVideo.map((image, index) =>
                    <img key={index} src={image} alt=""/>
                )}

                {this.state.items.map((item, index) => (
                    <div key={index}>
                        <div className="card">
                            <div className="card-title">
                                <h2>
                                    {item.name}
                                    <span>ID : {item.id}</span>
                                </h2>
                            </div>
                            <div className="card-img">
                                <img src={item.image} alt=""/>
                            </div>
                        </div>
                        Hello, {person.name} from {person.country}!
                    </div>
                ))}

                <button onClick = {this.nextSlide}>Next</button>
            </div>
        )
    }
}
