import React, { Component } from 'react';

export class Carousel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            currentImageIndex: 0,
            items: [
                {
                    id:1,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=first'
                },
                {
                    id:2,
                    name:'IVYSAUR',
                    image: 'https://via.placeholder.com/200x150?text=second'
                },
                {
                    id:3,
                    name:'VENUSAUR',
                    image: 'https://via.placeholder.com/200x150?text=third'
                },
                {
                    id:4,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=forth'
                },
                {
                    id:5,
                    name:'IVYSAUR',
                    image: 'https://via.placeholder.com/200x150?text=fifth'
                },
                {
                    id:6,
                    name:'VENUSAUR',
                    image: 'https://via.placeholder.com/200x150?text=sixth'
                },
                {
                    id:7,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=seventh'
                },
                {
                    id:8,
                    name:'IVYSAUR',
                    image: 'https://via.placeholder.com/200x150?text=eighth'
                },
                {
                    id:9,
                    name:'VENUSAUR',
                    image: 'https://via.placeholder.com/200x150?text=ninth'
                },
                {
                    id:10,
                    name:'BULBASAUR',
                    image: 'https://via.placeholder.com/200x150?text=tenth'
                }
            ],
            pokemonList : [],
            fetched : false,
            loading : false
        }
    }

    prevSlide = () => {
        // find the index of the last image in the array
        const lastIndex = this.state.items.length - 1;
        // check if we need to start over from the last index again
        const resetIndex = this.state.currentImageIndex === 0;
        const index = resetIndex ? lastIndex : this.state.currentImageIndex - 3;
        // assign the logical index to currentImageIndex that will use in render method
        this.setState({
            currentImageIndex: index
        })
    }

    nextSlide = () => {
        // find the index of the last image in the array
        const lastIndex = this.state.items.length - 1;
        // check if we need to start over from the first index
        const resetIndex = this.state.currentImageIndex === lastIndex;
        const index = resetIndex ? 0 : this.state.currentImageIndex + 3;
        // assign the logical index to currentImageIndex that will use in render method
        this.setState({
            currentImageIndex: index
        });
    }

    componentDidMount() {

        fetch('http://pokeapi.co/api/v2/pokemon?limit=151').then(res=>res.json())
            .then(response=>{
                this.setState({
                    pokemonList : response.results,
                    loading : true,
                    fetched : true
                });
            });
    }

    render() {
        const {fetched, loading, pokemonList} = this.state;
        console.log(pokemonList);
        // get current image index
        const index = this.state.currentImageIndex;
        let firstThreeItems = pokemonList.slice(index, index + 3);

        if (firstThreeItems.length < 3) {
            firstThreeItems = firstThreeItems.concat(this.state.items.slice(0, 3 - firstThreeItems.length))
        }

        if(fetched) {
            return (
                <div>...Loading</div>
            )
        } else {
            return (
                <div className='container'>
                    <div className="card-columns">
                        {firstThreeItems.map((item, index) => (
                            <div className="card" key={index}>
                                <div className="card-header bg-success">
                                    <span className="float-sm-left"> {item.name}  </span> <span className="text-right">ID : {item.id}</span>
                                </div>
                                <div className="card-body">
                                    <div className="card-img">
                                        <img src={item.image} alt=""/>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="row d-flex">
                        <div className="mr-auto">
                            <button onClick={this.prevSlide} className="btn btn-success">Previous</button>
                        </div>
                        <div className="p-2">
                            <button onClick={this.nextSlide} className="btn btn-success">Next</button>
                        </div>
                    </div>
                </div>
            )
        }
    }
}
